function calcCenaticAffinityRisk( sources ) {
var n_of_lic = program.getScenario().getConstraint( "#licenses", "st" );
var n_of_eq_lic = program.getScenario().getConstraint( "#equal-licenses", "st" );
var n_of_comp_lic = program.getScenario().getConstraint( "#compatible-licenses", "st" );
var n_of_lic_ty = program.getScenario().getConstraint( "#license-types", "st" );

var r = 1 - ((Number(n_of_eq_lic)/Number(n_of_lic)) + ((Number(n_of_comp_lic)/Number(n_of_lic)) * (1 /(Number(n_of_lic_ty) +1))));

return fx.e( r, 0 );
}
